DO $$
BEGIN
	IF to_regclass('public.content_tasks_id_seq') IS NOT NULL THEN
		EXECUTE 'ALTER SEQUENCE public.content_tasks_id_seq RESTART WITH 1';
	END IF;
END$$;

CREATE OR REPLACE FUNCTION public._fill_content_tasks_id()
RETURNS trigger AS $$
BEGIN
	IF NEW.id IS NULL THEN
		NEW.id := nextval('public.content_tasks_id_seq');
	END IF;

	IF NEW.content_type IS NULL OR btrim(NEW.content_type) = '' THEN
		NEW.content_type := COALESCE(NULLIF(NEW.task_type, ''), 'blog_post');
	END IF;

	RETURN NEW;
END;
$$ LANGUAGE plpgsql;

DROP TRIGGER IF EXISTS trg_content_tasks_fill_id ON public.content_tasks;
CREATE TRIGGER trg_content_tasks_fill_id
BEFORE INSERT ON public.content_tasks
FOR EACH ROW
EXECUTE FUNCTION public._fill_content_tasks_id();

